select

from IMPORT_PROJECTS

	1=1

		AND EMPLOYEE_ID = #{employeeId,jdbcType=DECIMAL}


		AND DEPARTMENT_ID = #{departmentId,jdbcType=DECIMAL}


		AND TRUNC(IMPORT_DATE,'MONTH') = #{importDate,jdbcType=TIMESTAMP}

select

from IMPORT_PROJECTS

	1=1

		AND EMPLOYEE_ID = #{employeeId,jdbcType=DECIMAL}


		AND DEPARTMENT_ID = #{departmentId,jdbcType=DECIMAL}

	 AND import_Date BETWEEN #{date1} AND #{date2}
	 AND import_Date >=#{date1}
	 AND import_Date <=#{date2}
